import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  ExclamationTriangleIcon,
  FireIcon,
  ChartBarIcon,
  BuildingOfficeIcon,
  ClockIcon,
  ArrowUpIcon,
  ArrowRightIcon
} from '@heroicons/react/24/outline';

export default function Dashboard() {
  const [dashboardData, setDashboardData] = useState(null);
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
    fetchAlerts();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const response = await fetch('/api/intelligence/dashboard');
      const data = await response.json();
      if (data.success) {
        setDashboardData(data.data);
      }
    } catch (error) {
      console.error('Failed to fetch dashboard data:', error);
    }
  };

  const fetchAlerts = async () => {
    try {
      const response = await fetch('/api/intelligence/alerts');
      const data = await response.json();
      if (data.success) {
        setAlerts(data.data.slice(0, 3)); // Show only top 3 alerts
      }
    } catch (error) {
      console.error('Failed to fetch alerts:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  const stats = [
    {
      name: 'Total Accounts',
      value: dashboardData?.totalAccounts || 0,
      change: '+2 this week',
      changeType: 'positive',
      icon: BuildingOfficeIcon
    },
    {
      name: 'High Priority',
      value: dashboardData?.highPriorityAccounts || 0,
      change: 'Need attention',
      changeType: 'warning',
      icon: FireIcon
    },
    {
      name: 'Active Alerts',
      value: dashboardData?.totalAlerts || 0,
      change: '2 urgent',
      changeType: 'negative',
      icon: ExclamationTriangleIcon
    },
    {
      name: 'Avg Urgency Score',
      value: dashboardData?.averageUrgencyScore || 0,
      change: '+5% this week',
      changeType: 'positive',
      icon: ChartBarIcon
    }
  ];

  const getAlertIcon = (urgency) => {
    if (urgency === 'high') return 'ðŸ”´';
    if (urgency === 'medium') return 'ðŸŸ¡';
    return 'ðŸŸ¢';
  };

  const getPriorityColor = (priority) => {
    if (priority === 'urgent') return 'text-red-600 bg-red-50';
    if (priority === 'high') return 'text-orange-600 bg-orange-50';
    return 'text-blue-600 bg-blue-50';
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="border-b border-gray-200 pb-5">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Sales Intelligence Dashboard</h1>
            <p className="mt-2 text-sm text-gray-600">
              Your AI guide through the sales wilderness â€¢ Last updated: {new Date().toLocaleTimeString()}
            </p>
          </div>
          <div className="flex items-center space-x-3">
            <div className="text-sm text-gray-500">
              Response Rate: <span className="font-semibold text-green-600">73%</span>
            </div>
            <div className="h-3 w-3 bg-green-500 rounded-full animate-pulse"></div>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-4">
        {stats.map((stat) => (
          <div key={stat.name} className="bg-white overflow-hidden shadow rounded-lg">
            <div className="p-5">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <stat.icon className="h-6 w-6 text-gray-400" />
                </div>
                <div className="ml-5 w-0 flex-1">
                  <dl>
                    <dt className="text-sm font-medium text-gray-500 truncate">
                      {stat.name}
                    </dt>
                    <dd className="flex items-baseline">
                      <div className="text-2xl font-semibold text-gray-900">
                        {stat.value}
                      </div>
                      <div className={`ml-2 flex items-baseline text-sm font-semibold ${
                        stat.changeType === 'positive' ? 'text-green-600' : 
                        stat.changeType === 'negative' ? 'text-red-600' : 'text-yellow-600'
                      }`}>
                        {stat.changeType === 'positive' && <ArrowUpIcon className="h-3 w-3 mr-1" />}
                        {stat.change}
                      </div>
                    </dd>
                  </dl>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Priority Actions */}
        <div className="lg:col-span-2 bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-medium text-gray-900">Priority Actions</h3>
            <p className="text-sm text-gray-500">AI-recommended next steps ranked by impact</p>
          </div>
          <div className="divide-y divide-gray-200">
            {dashboardData?.topActions?.map((action, index) => (
              <div key={index} className="px-6 py-4 hover:bg-gray-50">
                <div className="flex items-start space-x-3">
                  <div className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getPriorityColor(action.priority)}`}>
                    {action.priority.toUpperCase()}
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900">
                      {action.action}
                    </p>
                    <div className="mt-1 flex items-center space-x-4 text-xs text-gray-500">
                      <span>Account: {action.account}</span>
                      <span>Due: {new Date(action.deadline).toLocaleDateString()}</span>
                    </div>
                  </div>
                  <ArrowRightIcon className="h-4 w-4 text-gray-400" />
                </div>
              </div>
            ))}
          </div>
          <div className="px-6 py-3 border-t border-gray-200">
            <Link to="/intelligence" className="text-sm font-medium text-blue-600 hover:text-blue-500">
              View all recommendations â†’
            </Link>
          </div>
        </div>

        {/* Active Alerts */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-medium text-gray-900">Active Alerts</h3>
            <p className="text-sm text-gray-500">Real-time intelligence signals</p>
          </div>
          <div className="divide-y divide-gray-200">
            {alerts.map((alert) => (
              <div key={alert.id} className="px-6 py-4">
                <div className="flex items-start space-x-3">
                  <span className="text-lg">{getAlertIcon(alert.urgency)}</span>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900">
                      {alert.accountName}
                    </p>
                    <p className="text-sm text-gray-600 mt-1">
                      {alert.message}
                    </p>
                    <div className="mt-2 text-xs text-gray-500">
                      {new Date(alert.timestamp).toLocaleTimeString()}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="px-6 py-3 border-t border-gray-200">
            <Link to="/intelligence" className="text-sm font-medium text-blue-600 hover:text-blue-500">
              View all alerts â†’
            </Link>
          </div>
        </div>
      </div>

      {/* Recent Activity & Quick Stats */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Activity */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-medium text-gray-900">Recent Activity</h3>
            <p className="text-sm text-gray-500">Latest platform updates</p>
          </div>
          <div className="divide-y divide-gray-200">
            {dashboardData?.recentActivities?.map((activity, index) => (
              <div key={index} className="px-6 py-4">
                <div className="flex items-center space-x-3">
                  <div className={`h-2 w-2 rounded-full ${
                    activity.type === 'alert' ? 'bg-red-500' :
                    activity.type === 'news' ? 'bg-blue-500' : 'bg-green-500'
                  }`}></div>
                  <div className="flex-1">
                    <p className="text-sm text-gray-900">{activity.message}</p>
                    <div className="flex items-center mt-1 text-xs text-gray-500">
                      <ClockIcon className="h-3 w-3 mr-1" />
                      {new Date(activity.timestamp).toLocaleString()}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Weekly Performance */}
        <div className="bg-white shadow rounded-lg">
          <div className="px-6 py-4 border-b border-gray-200">
            <h3 className="text-lg font-medium text-gray-900">This Week's Performance</h3>
            <p className="text-sm text-gray-500">Key metrics and progress</p>
          </div>
          <div className="px-6 py-4 space-y-4">
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">New Opportunities</span>
              <span className="text-lg font-semibold text-gray-900">{dashboardData?.weeklyStats?.newOpportunities}</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">Activities Completed</span>
              <span className="text-lg font-semibold text-gray-900">{dashboardData?.weeklyStats?.completedActivities}</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">Introductions Requested</span>
              <span className="text-lg font-semibold text-gray-900">{dashboardData?.weeklyStats?.introductionsRequested}</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">Response Rate</span>
              <span className="text-lg font-semibold text-green-600">
                {Math.round(dashboardData?.weeklyStats?.responseRate * 100)}%
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 rounded-lg shadow-lg">
        <div className="px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-medium text-white">Ready to take action?</h3>
              <p className="text-blue-100">View your prioritized account list or explore relationship opportunities</p>
            </div>
            <div className="flex space-x-3">
              <Link
                to="/accounts"
                className="bg-white text-blue-600 px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-50 transition-colors"
              >
                View Accounts
              </Link>
              <Link
                to="/relationships"
                className="bg-blue-500 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-400 transition-colors"
              >
                Explore Relationships
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
